// ========================================
// ENHANCED FREE ANALYSIS - PROFESSIONAL GRADE
// Works with ANY data, NEW or RECURRING reports
// ========================================

const data = $input.first().json;
const items = data.allItems;

// ========================================
// STEP 1: AUTO-DETECT DATA STRUCTURE
// ========================================

const sampleItem = items[0];
const allColumns = Object.keys(sampleItem);

// Auto-detect VALUE column (Sales, Amount, Revenue, Value, Quantity, Price)
const valueColumn = allColumns.find(col => {
  const lower = col.toLowerCase();
  return lower.includes('sales') || lower.includes('amount') || 
         lower.includes('revenue') || lower.includes('value') || 
         lower.includes('price') || lower.includes('total');
}) || allColumns.find(col => !isNaN(parseFloat(sampleItem[col])));

// Auto-detect CATEGORY columns
const categoryColumns = allColumns.filter(col => {
  const lower = col.toLowerCase();
  const value = sampleItem[col];
  return (lower.includes('product') || lower.includes('client') || 
          lower.includes('item') || lower.includes('category') || 
          lower.includes('type') || lower.includes('name') ||
          lower.includes('service') || lower.includes('status')) &&
         typeof value === 'string';
});

// Auto-detect REGION/LOCATION column
const locationColumn = allColumns.find(col => {
  const lower = col.toLowerCase();
  return lower.includes('region') || lower.includes('location') || 
         lower.includes('area') || lower.includes('zone') ||
         lower.includes('city') || lower.includes('warehouse');
});

// Auto-detect TIME column
const timeColumn = allColumns.find(col => {
  const lower = col.toLowerCase();
  return lower.includes('month') || lower.includes('date') || 
         lower.includes('period') || lower.includes('week') ||
         lower.includes('time') || lower.includes('day');
});

// Auto-detect PRIORITY/STATUS column
const priorityColumn = allColumns.find(col => {
  const lower = col.toLowerCase();
  return lower.includes('priority') || lower.includes('status') ||
         lower.includes('stage') || lower.includes('level');
});

// ========================================
// STEP 2: CALCULATE CORE METRICS
// ========================================

// Using existing chart data for main analysis
const chart1Label = data.lineChart.labels[0] ? 'Time Period' : 'Period';
const chart2Label = categoryColumns[0] || 'Category';
const chart3Label = locationColumn || categoryColumns[1] || 'Segment';

const trendData = data.lineChart.data;
const chart2Data = data.barChart.data;
const chart3Data = data.pieChart.data;

// ========================================
// STEP 3: TREND ANALYSIS (Within current dataset)
// ========================================

const firstValue = trendData[0];
const lastValue = trendData[trendData.length - 1];
const midValue = trendData[Math.floor(trendData.length / 2)];

const overallTrend = lastValue > firstValue ? 'increasing' : 'decreasing';
const trendPercent = firstValue !== 0 
  ? Math.abs(((lastValue - firstValue) / firstValue) * 100).toFixed(1)
  : 0;

// Detect acceleration/deceleration
const firstHalfAvg = trendData.slice(0, Math.floor(trendData.length / 2))
  .reduce((a, b) => a + b, 0) / Math.floor(trendData.length / 2);
const secondHalfAvg = trendData.slice(Math.floor(trendData.length / 2))
  .reduce((a, b) => a + b, 0) / Math.ceil(trendData.length / 2);
const momentum = secondHalfAvg > firstHalfAvg ? 'accelerating' : 'decelerating';

// ========================================
// STEP 4: PERFORMANCE RANKING & SEGMENTATION
// ========================================

// Rank performers in Chart 2 (Products/Clients/Categories)
const chart2WithLabels = data.barChart.labels.map((label, i) => ({
  name: label,
  value: chart2Data[i],
  percent: ((chart2Data[i] / data.totalSales) * 100).toFixed(1)
})).sort((a, b) => b.value - a.value);

const topPerformer = chart2WithLabels[0];
const secondBest = chart2WithLabels[1];
const thirdBest = chart2WithLabels[2];
const needsAttention = chart2WithLabels[chart2WithLabels.length - 1];

// Calculate top 3 concentration
const top3Total = (topPerformer.value + secondBest.value + thirdBest.value);
const top3Percent = ((top3Total / data.totalSales) * 100).toFixed(1);

// ========================================
// STEP 5: PARETO ANALYSIS (80/20 Rule)
// ========================================

let cumulativeValue = 0;
let pareto80Count = 0;
for (let i = 0; i < chart2WithLabels.length; i++) {
  cumulativeValue += chart2WithLabels[i].value;
  pareto80Count++;
  if (cumulativeValue >= data.totalSales * 0.8) break;
}
const pareto80Percent = ((pareto80Count / chart2WithLabels.length) * 100).toFixed(0);

// ========================================
// STEP 6: REGIONAL/SEGMENT ANALYSIS
// ========================================

const chart3WithLabels = data.pieChart.labels.map((label, i) => ({
  name: label,
  value: chart3Data[i],
  percent: ((chart3Data[i] / data.totalSales) * 100).toFixed(1)
})).sort((a, b) => b.value - a.value);

const topRegion = chart3WithLabels[0];
const weakestRegion = chart3WithLabels[chart3WithLabels.length - 1];

// Calculate regional balance (coefficient of variation)
const regionMean = data.totalSales / chart3Data.length;
const regionVariance = chart3Data.reduce((sum, val) => 
  sum + Math.pow(val - regionMean, 2), 0) / chart3Data.length;
const regionCV = Math.sqrt(regionVariance) / regionMean;
const balanceLevel = regionCV < 0.3 ? 'well-balanced' : 
                     regionCV < 0.6 ? 'moderately balanced' : 'highly concentrated';

// ========================================
// STEP 7: ANOMALY DETECTION
// ========================================

// Detect unusual spikes/drops in trend data
const trendMean = trendData.reduce((a, b) => a + b, 0) / trendData.length;
const trendStdDev = Math.sqrt(
  trendData.reduce((sum, val) => sum + Math.pow(val - trendMean, 2), 0) / trendData.length
);

const anomalies = [];
trendData.forEach((val, i) => {
  const zScore = Math.abs((val - trendMean) / trendStdDev);
  if (zScore > 2) { // More than 2 standard deviations
    anomalies.push({
      period: data.lineChart.labels[i],
      value: val,
      type: val > trendMean ? 'spike' : 'drop',
      deviation: ((Math.abs(val - trendMean) / trendMean) * 100).toFixed(1)
    });
  }
});

// ========================================
// STEP 8: PERFORMANCE SEGMENTATION
// ========================================

// Categorize into High/Medium/Low performers
const threshold75 = data.totalSales * 0.75 / chart2Data.length;
const threshold25 = data.totalSales * 0.25 / chart2Data.length;

const highPerformers = chart2WithLabels.filter(item => item.value > threshold75);
const mediumPerformers = chart2WithLabels.filter(item => 
  item.value <= threshold75 && item.value >= threshold25);
const lowPerformers = chart2WithLabels.filter(item => item.value < threshold25);

// ========================================
// STEP 9: GROWTH INDICATORS (Within dataset)
// ========================================

// Compare first period vs last period
const firstPeriodAvg = trendData.slice(0, 2).reduce((a, b) => a + b, 0) / 2;
const lastPeriodAvg = trendData.slice(-2).reduce((a, b) => a + b, 0) / 2;
const internalGrowth = ((lastPeriodAvg - firstPeriodAvg) / firstPeriodAvg * 100).toFixed(1);

// ========================================
// STEP 10: GENERATE EXECUTIVE SUMMARY
// ========================================

const executiveSummary = `Performance analysis of ${data.recordCount} transactions reveals ${overallTrend} trend with ${trendPercent}% ${overallTrend === 'increasing' ? 'growth' : 'decline'}, ${momentum} in recent periods. Total ${valueColumn || 'value'} of ‚Çπ${Math.round(data.totalSales).toLocaleString('en-IN')} generated with ‚Çπ${Math.round(data.avgSales).toLocaleString('en-IN')} average transaction value. ${topPerformer.name} leads ${chart2Label} performance commanding ${topPerformer.percent}% market share, while ${topRegion.name} dominates ${chart3Label} distribution with ${topRegion.percent}% contribution. Market concentration shows ${pareto80Percent}% of ${chart2Label} driving 80% of results (Pareto principle).`;

// ========================================
// STEP 11: GENERATE KEY INSIGHTS
// ========================================

const keyInsights = [
  `${overallTrend === 'increasing' ? 'üìà Positive momentum' : 'üìâ Performance decline'}: ${overallTrend} by ${trendPercent}% with ${momentum} trend in latter half`,

  `üèÜ ${topPerformer.name} dominates with ${topPerformer.percent}% share (‚Çπ${Math.round(topPerformer.value).toLocaleString('en-IN')}), followed by ${secondBest.name} at ${secondBest.percent}%`,

  `${chart3Label} distribution is ${balanceLevel} - ${topRegion.name} leads at ${topRegion.percent}%, ${weakestRegion.name} trails at ${weakestRegion.percent}%`,

  anomalies.length > 0 
    ? `‚ö†Ô∏è ${anomalies.length} significant ${anomalies.length > 1 ? 'anomalies' : 'anomaly'} detected: ${anomalies[0].type} of ${anomalies[0].deviation}% in ${anomalies[0].period}`
    : `‚úÖ Performance stable with no significant anomalies detected`,

  `üìä Top 3 ${chart2Label} contribute ${top3Percent}% of total, indicating ${top3Percent > 60 ? 'high concentration risk' : 'healthy diversification'}`
];

// ========================================
// STEP 12: TOP PERFORMER ANALYSIS
// ========================================

const topPerformerAnalysis = `${topPerformer.name} leads the ${chart2Label} category with ‚Çπ${Math.round(topPerformer.value).toLocaleString('en-IN')}, representing ${topPerformer.percent}% of total ${valueColumn || 'value'}. This ${topPerformer.percent > 30 ? 'dominant' : 'strong'} position makes it a critical driver of overall performance. ${secondBest.name} (${secondBest.percent}%) and ${thirdBest.name} (${thirdBest.percent}%) complete the top tier, collectively accounting for ${top3Percent}% of results. Strategic focus on these key performers can yield ${top3Percent > 60 ? 'significant' : 'substantial'} returns with concentrated effort.`;

// ========================================
// STEP 13: NEEDS ATTENTION ANALYSIS
// ========================================

const needsAttentionAnalysis = `${needsAttention.name} shows the weakest performance in ${chart2Label} category with ‚Çπ${Math.round(needsAttention.value).toLocaleString('en-IN')} (${needsAttention.percent}% share), indicating ${needsAttention.percent < 5 ? 'critical underperformance' : 'suboptimal results'}. ${lowPerformers.length > 1 ? `Along with ${lowPerformers.length - 1} other low performers, this` : 'This'} segment requires immediate strategic intervention. Gap analysis shows ${((topPerformer.value - needsAttention.value) / needsAttention.value * 100).toFixed(0)}x performance differential versus top performer, suggesting significant improvement potential through targeted initiatives.`;

// ========================================
// STEP 14: STRATEGIC RECOMMENDATIONS
// ========================================

const recommendations = [
  `Maximize ROI by allocating 60% of resources to top 3 performers (${topPerformer.name}, ${secondBest.name}, ${thirdBest.name}) which drive ${top3Percent}% of revenue`,

  `Implement turnaround strategy for ${needsAttention.name} ${lowPerformers.length > 1 ? `and ${lowPerformers.length - 1} other underperformers` : ''} with performance improvement target of ${Math.round(threshold25 / needsAttention.value * 100 - 100)}% to reach baseline`,

  `${regionCV > 0.5 ? `Address regional imbalance by replicating ${topRegion.name} success factors in ${weakestRegion.name} (potential ‚Çπ${Math.round((topRegion.value - weakestRegion.value) * 0.5).toLocaleString('en-IN')} opportunity)` : `Maintain balanced regional distribution while scaling ${topRegion.name} operations`}`,

  overallTrend === 'increasing' 
    ? `Sustain ${momentum} growth momentum through continued investment in proven strategies and gradual capacity expansion`
    : `Urgent: Deploy corrective measures to reverse ${trendPercent}% decline - conduct root cause analysis and implement recovery plan within 2 weeks`,

  anomalies.length > 0
    ? `Investigate ${anomalies[0].period} ${anomalies[0].type} (${anomalies[0].deviation}% deviation) to identify success factors or prevent recurrence`
    : `Establish monitoring framework to detect early warning signals and maintain current stability`
];

// ========================================
// STEP 15: OVERALL CONCLUSION
// ========================================

const conclusion = `Comprehensive analysis indicates ${overallTrend === 'increasing' ? 'positive trajectory' : 'performance challenges requiring immediate attention'} with clear stratification across ${chart2Label} segments. ${topPerformer.name}'s ${topPerformer.percent}% dominance and ${topRegion.name}'s ${topRegion.percent}% regional leadership demonstrate proven success patterns that should be leveraged. However, ${pareto80Percent}% concentration in top performers ${pareto80Percent < 40 ? 'indicates healthy diversification' : 'presents concentration risk requiring mitigation'}. ${overallTrend === 'increasing' ? `With ${momentum} momentum and ${internalGrowth > 0 ? internalGrowth + '%' : 'stable'} internal growth, strategic focus on scaling successes while nurturing underperformers will optimize portfolio performance.` : `Declining ${trendPercent}% trend with ${lowPerformers.length} underperforming segments demands immediate strategic intervention combining defensive measures (cost optimization, efficiency gains) with offensive tactics (market expansion, product innovation). Recovery timeline: 4-6 weeks with focused execution.`} Key success factors: ${top3Percent > 60 ? 'diversify revenue base' : 'maintain current balance'}, strengthen ${weakestRegion.name} region, and ${overallTrend === 'increasing' ? 'capitalize on growth momentum' : 'execute turnaround plan with urgency'}.`;

// ========================================
// STEP 16: RETURN ENRICHED DATA
// ========================================

return {
  json: {
    // Pass through all original data
    ...data,

    // Add comprehensive insights
    insights: {
      executiveSummary: executiveSummary,
      keyInsights: keyInsights,
      topPerformer: topPerformerAnalysis,
      needsAttention: needsAttentionAnalysis,
      recommendations: recommendations,
      conclusion: conclusion
    },

    // Add detailed metrics for reference
    analytics: {
